public:
    Polynomial():islegal(false){}

    Polynomial(const string& input):islegal(true){
        int n=input.size(),i=0;
        while(i<n){
            while(i<n && input[i]==' ') i++;
            if(i>=n) break;
            bool neg=false;
            if(input[i]=='-'){
                neg=true;
                i++;
            }
            else if(input[i]=='+') i++;
            int j=i;
            bool hasPoint=false;
            while(j<n && ((input[j]>='0' && input[j]<='9') || input[j]=='.')){
                if(input[j]=='.'){
                    if(hasPoint){
                        islegal=false;
                        break;
                    }
                    hasPoint=true;
                }
                j++;
            }
            if(!islegal) break;
            //系数项
            double coeff;
            if(j>i) coeff=stod(input.substr(i,j-i));
            else coeff=1;
            if(neg) coeff=-coeff;

            int exp=0;
            if(j<n && (input[j]=='x' || input[j]=='X')){
                j++;
                if(j<n && input[j]=='^'){
                    j++;
                    int k=j;
                    while(k<n && input[k]>='0' && input[k]<='9') k++;
                    if(k>j){
                        exp=stoi(input.substr(j,k-j));
                        j=k;
                    }
                    else{
                        islegal=false;
                        break;
                    }
                }
                else{
                    if(j<n && '0'<=input[j] && input[j]<='9'){
                        islegal=false;
                        break;
                    }
                    exp=1;
                }
            }
            else exp=0;
            mp[exp]+=coeff;
            i=j;
        }
    }

    Polynomial(const map<int,double>& m):islegal(true),mp(m){}

    Polynomial operator+(const Polynomial& other){
        map<int,double> res=mp;
        for(auto it=other.mp.begin();it!=other.mp.end();++it){
            int exp=it->first;
            double coeff=it->second;
            res[exp]+=coeff;
        }
        return Polynomial(res);
    }

    Polynomial operator-(const Polynomial& other){
        map<int,double> res=mp;
        for(auto it=other.mp.begin();it!=other.mp.end();++it){
            int exp=it->first;
            double coeff=it->second;
            res[exp]-=coeff;
        }
        return Polynomial(res);
    }

    Polynomial operator*(const Polynomial& other){//多项式乘法
        map<int,double> res;
        for(auto it1=mp.begin();it1!=mp.end();++it1){
            int exp1=it1->first;
            double coeff1=it1->second;
            for(auto it2=other.mp.begin();it2!=other.mp.end();++it2){
                int exp2=it2->first;
                double coeff2=it2->second;
                int exp=exp1+exp2;
                double coeff=coeff1*coeff2;
                res[exp]+=coeff;
            }
        }
        return Polynomial(res);
    }

    void print(){
