};

Polynomial evaluate(const string& input, bool& ok) {
    if(!ok) return Polynomial();
    string s=input;
    while(s.length()>2 && s.front()=='(' && s.back()==')') {
        int bracketCnt=0;
        bool balanced=true;
        for(int i=0;i<s.length()-1;i++){
            if(s[i]=='(') bracketCnt++;
            else if(s[i]==')') bracketCnt--;
            if(bracketCnt==0) {
                balanced=false;
                break;
            }
        }
        if(balanced) s=s.substr(1,s.length()-2);
        else break;
    }

    //从右到左找加减
    int bracketCnt=0;
    for(int i=s.size()-1;i>=0;i--){
        if(s[i]==')') bracketCnt++;
        else if(s[i]=='(') bracketCnt--;
        else if(bracketCnt==0 && (s[i]=='+' || s[i]=='-')){
            Polynomial left(evaluate(s.substr(0,i),ok));
            Polynomial right(evaluate(s.substr(i+1),ok));
            if(!ok) return Polynomial();
            if(s[i]=='+') return left+right;
            else return left-right;
        }
    }

    //无加减,从右到左找乘
    bracketCnt=0;
    for(int i=s.size()-1;i>=0;i--){
        if(s[i]==')') bracketCnt++;
        else if(s[i]=='(') bracketCnt--;
        else if(bracketCnt==0 && s[i]=='*'){
            Polynomial left(evaluate(s.substr(0,i),ok));
            Polynomial right(evaluate(s.substr(i+1),ok));
            if(!ok) return Polynomial();
            return left*right;
        }
    }
    //无运算符
    Polynomial p(s);
    if(!p.get_islegal()) ok=false;
    return p;
}

string extract(const string& input){
    string res;
    for(auto c:input){
        if(('0'<=c && c<='9')||c==' '||c=='+'||c=='-'||c=='*'||c=='('||c==')'||c=='^'||c=='.'||c=='x'||c=='X'){
            res+=c;
        }
    }
    return res;
}

int solve(){
    string input;
    getline(cin,input);
    string processed=extract(input);
    int bracketCnt=0;
    for(auto c:processed){
        if(c=='(') bracketCnt++;
        else if(c==')') bracketCnt--;
        if(bracketCnt<0){
            cout<<"格式有误（括号不匹配）"<<endl;
            return -1;
        }
    }
    if(bracketCnt>0){
        cout<<"格式有误（括号不匹配）"<<endl;
        return -1;
    }
    bool ok=true;
    Polynomial res(evaluate(processed,ok));
    if(!ok){
        cout<<"非法输入或格式错误"<<endl;
        return -1;
    }
    cout<<"=";
    res.print();
    cout<<endl;
    return 0;
}

int main(){
    cout<<"计算器将忽略空格及非法字符"<<endl;
    cout<<"示例：(2x+5x^8-3.1x^11)+(7-5x^8*11x^9)"<<endl;
    cout<<"请输入多项式运算表达式进行计算"<<endl;
    while(true){
        if(solve()==-1) cout<<"请重新输入:";
        else break;
    }
    system("pause");
    return 0;
}


