// filepath: c:\Users\DRORPOL\code\israel2025\frontend\src\components\charts\components\BaseChart\useChartOption.ts
import { grey, purple } from '@mui/material/colors'
import { useTheme } from '@mui/material/styles'
import { deepmerge } from '@mui/utils'
import type { EChartsOption } from 'echarts'
import { borderBottomWidth } from 'html2canvas/dist/types/css/property-descriptors/border-width'
import { isArray, isNumber } from 'lodash'

import { formatCategory, formatCategoryOne } from '@/components/tables/utils'
import { useChartInteraction } from '@/contexts/ChartInteractionContext'
import { useText } from '@/i18n/useText'
import { useStore } from '@/store/useStore'
import { colors } from '@/styling/colors'
import { titleColor } from '@/theme/commonTheme'

import { addAmericanIdol, addMarklines } from './marklines'
import { genericOption } from './option'
import type { ChartData, CreateChartOptionProps } from './types'
import {
  applyAnomalyStyleIfNeeded,
  createEnhancedTooltipFormatter,
  createLabelWithIcon,
  createLowQuantityPattern,
  createRichIconStyle,
  createSignificanceTooltipFormatter,
  createTotalBarStyle,
  createTotalLabelWithIcon,
  formatDateRange,
  hasLowQuantityDataPoints,
  hasSamplingErrorData,
  isLowQuantityDataPoint,
} from './utils'

// Todo: what useChartOption needs to deepmerge is only the dynamic stuff
// everything that's generic and not dynamic move to the generic staitc option object.

// Constants
const TOTAL_SERIES_NAME = 'כולל'

// Helper function to truncate long category text
const truncateText = (text: string, maxWords: number = 7): string => {
  const words = text.split(' ')
  if (words.length <= maxWords) return text
  return words.slice(0, maxWords).join(' ') + '...'
}

export const useChartOption = ({
  chartData,
  options = {},
}: CreateChartOptionProps): EChartsOption | undefined => {
  const theme = useTheme()

  const {
    showLegend = true,
    startDateOnly = false,
    showMonthBelowDate = false,
    title,
    seriesLabelPosition = 'top',
    labelFormatter,
    subTitle,
    marklines = false,
    americanIdol = false,
    legendFormatter,
    denseLegend = false,
    noBarGap = false,
    wrapSubtitle = false,
    horizontal = false,
    xAxisFormatter,
    showAllCategories = false,
    hideGridLines = false,
    accumulate = false,
    trueCategories = false,
    toolbox = false,
    stacked = false,
  } = options

  const sectionExpanded = useStore(state => state.sectionExpanded)
  // this is the new sectionExpanded: true when in ChartPage
  const isInteractive = useChartInteraction()

  const { markLine } = useText()

  if (!chartData || !chartData.series) {
    return undefined
  }

  // Limit to 7 most recent categories if there are more than 7 (unless showAllCategories is true)
  let categories = chartData.categories
  let series = chartData.series

  // if (!showAllCategories && categories.length > 7) {
  //   // Take the last 7 categories (most recent)
  //   categories = categories.slice(-7)

  //   // Update series data to match the limited categories
  //   series = chartData.series.map(s => ({
  //     ...s,
  //     data: Array.isArray(s.data) ? s.data.slice(-7) : s.data,
  //   }))
  // }

  let yAxis = chartData.yAxis
  let grid = chartData.grid || {} // Start with grid from chartData

  if (seriesLabelPosition) {
    console.log('🚀 ~ seriesLabelPosition:', seriesLabelPosition)
    series = series.map((s, index) =>
      s.type === 'bar'
        ? {
            ...s,
            label: {
              show: true,
              position: seriesLabelPosition,
              formatter: (() => {
                const isTotalSeries = s.name === TOTAL_SERIES_NAME
                const hasLowQuantity = hasLowQuantityDataPoints(s.quantity)

                // Use valueFormatter if available, otherwise labelFormatter, otherwise default
                const baseFormatter =
                  options.valueFormatter || labelFormatter || '{c}'

                if (isTotalSeries) {
                  return createTotalLabelWithIcon(baseFormatter)
                } else if (hasLowQuantity) {
                  return createLabelWithIcon(
                    (dataIndex: number) =>
                      isLowQuantityDataPoint(s.quantity, dataIndex),
                    baseFormatter,
                  )
                } else {
                  // Apply the formatter as a function if it's a string template
                  if (typeof baseFormatter === 'string') {
                    return function (params: any) {
                      return baseFormatter
                        .replace('{c}', params.value)
                        .replace('{value}', params.value)
                    }
                  } else {
                    return baseFormatter
                  }
                }
              })(),
              color: (() => {
                console.log(
                  '🚀 ~ inside color. seriesLabelPosition:',
                  seriesLabelPosition,
                )
                const isTotalSeries = s.name === TOTAL_SERIES_NAME

                if (isTotalSeries) {
                  // Fixed color for total bar labels - greyish white to match border
                  return grey[200]
                } else if (seriesLabelPosition === 'inside') {
                  console.log('returning black')
                  return '#000000'
                } else {
                  return s.color || '#ffffff'
                }
              })(),
              borderWidth: 0,
              borderColor: 'transparent',
              backgroundColor: 'transparent',
            },
          }
        : s,
    )
    // Apply grid adjustments based on y-axis visibility (set by transformApiToChartData)
    if (seriesLabelPosition && yAxis && yAxis.show === false) {
      grid = { ...grid, left: -20 } // Merge with existing grid instead of overriding
    }
  }

  // Ensure yAxis is always defined for vertical charts (ECharts requires it)
  // If yAxis is undefined/null/empty, set a default
  if (
    !yAxis ||
    (Array.isArray(yAxis) && yAxis.length === 0) ||
    (typeof yAxis === 'object' && Object.keys(yAxis).length === 0)
  ) {
    yAxis = { type: 'value' }
  }

  // Apply no bar gap if requested
  if (noBarGap) {
    series = series.map(s => (s.type === 'bar' ? { ...s, barGap: '0%' } : s))
  }

  // Limit maximum bar width for all bar charts across the app
  series = series.map(s =>
    s.type === 'bar' ? { ...s, barMaxWidth: '20%' } : s,
  )

  // Apply visual indicators for low quantity and total series
  series = series.map(s => {
    const isTotalSeries = s.name === TOTAL_SERIES_NAME

    // Prioritize total series styling over low quantity styling
    if (isTotalSeries) {
      return {
        ...s,
        itemStyle: {
          ...s.itemStyle,
          ...createTotalBarStyle(), // Use default parameter from function
        },
      }
    }

    if (hasLowQuantityDataPoints(s.quantity)) {
      return {
        ...s,
        itemStyle: {
          ...s.itemStyle,
          color: function (params: any) {
            if (isLowQuantityDataPoint(s.quantity, params.dataIndex)) {
              return createLowQuantityPattern(params.color)
            }
            return params.color
          },
        },
      }
    }

    return s
  })

  // Add cumulative series if accumulate is true and there's exactly one series
  if (accumulate && series.length === 1) {
    const originalSeries = series[0]
    const cumulativeData: number[] = []
    let runningSum = 0

    // Calculate cumulative values
    if (Array.isArray(originalSeries.data)) {
      originalSeries.data.forEach((value: number | null) => {
        if (value !== null && value !== undefined) {
          runningSum += value
          cumulativeData.push(runningSum)
        } else {
          cumulativeData.push(null)
        }
      })
    }

    // Add the cumulative series
    const cumulativeSeries = {
      ...originalSeries,
      id: originalSeries.id ? `${originalSeries.id}_cumulative` : 'cumulative',
      name: 'מצטבר',
      data: cumulativeData,
      type: 'bar', // Use line chart for cumulative data
      color: purple['A400'],
      calculated: true,
    }

    series = [originalSeries, cumulativeSeries]
  }

  const textColor = theme.palette.text.primary
  const subTextColor = theme.palette.text.secondary

  // if (marklines) {
  //   series = addMarklines(series, categories, markLine)
  // }

  if (americanIdol) {
    series = addAmericanIdol(series, categories)
  }

  const dynamicOptions = {
    title: {
      text: title,
      left: 'center',
      subtext: Array.isArray(subTitle) ? subTitle.join('\n') : subTitle,
      textStyle: {
        color: titleColor,
        fontSize: '1.3rem',
      },
      padding: [-10, 0, 0, 0],
      subtextStyle: {
        fontSize: '0.95rem',
        color: subTextColor,
        ...(wrapSubtitle
          ? {
              width: 550,
              overflow: 'break',
              lineHeight: 22,
            }
          : {
              width: 1000, // Default behavior
              overflow: 'none',
            }),
      },
      ...(wrapSubtitle
        ? {
            itemGap: 5, // Reduce spacing between title and subtitle when wrapping
          }
        : {}),
    },
    legend: {
      show: showLegend,
      textStyle: { color: textColor },
      formatter: legendFormatter,
      itemGap: denseLegend ? 15 : 44,
      ...(wrapSubtitle && showLegend
        ? {
            top: 110, // Position legend lower when subtitle wrapping is enabled
          }
        : {}),
    },
    grid: {
      ...grid,
      ...(wrapSubtitle
        ? {
            top: showLegend ? 140 : 110, // Adjust grid top when subtitle wrapping is enabled
          }
        : {}),
      ...(horizontal
        ? {
            left: '0%', // Reduce left margin so bars start closer to the names
            right: '5%', // Give some breathing room on the right
            width: '95%', // Increase total width to use more space
          }
        : {}),
      bottom: sectionExpanded ? '15%' : '0%', // Reverted to preserve chart height
    },
    tooltip: {
      trigger: hasSamplingErrorData(series) ? 'item' : 'axis',
      formatter: hasSamplingErrorData(series)
        ? createSignificanceTooltipFormatter(
            options.valueFormatter || options.labelFormatter,
            series as any,
          )
        : createEnhancedTooltipFormatter(
            options.valueFormatter || options.labelFormatter,
            series,
          ),
    },
    ...(horizontal
      ? {
          // For horizontal charts, swap xAxis and yAxis
          xAxis: {
            type: 'value',
            splitLine: {
              show: false, // Hide vertical grid lines
            },
            ...(xAxisFormatter
              ? {
                  axisLabel: {
                    formatter: xAxisFormatter,
                  },
                }
              : {}),
          },
          yAxis: {
            type: 'category',
            data: categories.map(cat => {
              // Check if category looks like a date range (contains ~ and dates)
              if (cat.includes('~') && cat.includes('-')) {
                return formatDateRange(cat, startDateOnly, showMonthBelowDate)
              }
              // Otherwise, return the category as-is (for non-date categories like names)
              return cat
            }),
            inverse: true, // Reverse order so highest values appear at top
          },
        }
      : {
          // For vertical charts, use normal xAxis and yAxis
          xAxis: {
            type: chartData.xAxisType || 'category', // Use xAxisType from API data, fallback to 'category'
            ...(chartData.xAxisType !== 'time' && {
              data: categories.map(cat => {
                // Check if category looks like a date range (contains ~ and dates)
                if (cat.includes('~') && cat.includes('-')) {
                  return formatDateRange(cat, startDateOnly, showMonthBelowDate)
                }
                // Truncate long category text to prevent overflow
                return truncateText(cat, 7) // Max 7 words
              }),
            }),
            ...(chartData.xAxisType === 'time' && {
              axisLabel: {
                formatter: {
                  year: '{yyyy}',
                  month: '{MMM} \n{yyyy}',
                  day: '{d} {MMM}',
                  hour: '{HH}:{mm}',
                  minute: '{HH}:{mm}',
                  second: '{HH}:{mm}:{ss}',
                  millisecond: '{hh}:{mm}:{ss} {SSS}',
                  none: ' ',
                },
              },
              axisTick: {
                alignWithLabel: true,
              },
              splitLine: {
                show: false,
              },
            }),
            ...(chartData.xAxisType !== 'time' && {
              axisLabel: {
                rotate: options.trueCategories
                  ? 0
                  : categories?.some(cat => !cat.includes('~'))
                    ? 90
                    : 0,
